import React, { useState, useEffect } from 'react';
import { membersAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import './Members.css';

function Members() {
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingMember, setEditingMember] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    contact: '',
    age: '',
    date_of_birth: '',
    gender: '',
    address: '',
    photo_url: '',
    membership_type: 'Monthly',
    start_date: new Date().toISOString().split('T')[0],
    end_date: '',
    notes: ''
  });

  useEffect(() => {
    loadMembers();
  }, []);

  const loadMembers = async () => {
    try {
      const response = await membersAPI.getAll();
      setMembers(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to load members');
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingMember) {
        await membersAPI.update(editingMember._id, formData);
      } else {
        await membersAPI.create(formData);
      }
      setShowModal(false);
      resetForm();
      loadMembers();
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to save member');
    }
  };

  const handleEdit = (member) => {
    setEditingMember(member);
    setFormData({
      name: member.name,
      email: member.email || '',
      contact: member.contact,
      age: member.age || '',
      date_of_birth: member.date_of_birth ? member.date_of_birth.split('T')[0] : '',
      gender: member.gender || '',
      address: member.address || '',
      photo_url: member.photo_url || '',
      membership_type: member.membership_type,
      start_date: member.start_date.split('T')[0],
      end_date: member.end_date.split('T')[0],
      notes: member.notes || ''
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this member?')) {
      try {
        await membersAPI.delete(id);
        loadMembers();
      } catch (err) {
        alert('Failed to delete member');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      contact: '',
      age: '',
      date_of_birth: '',
      gender: '',
      address: '',
      photo_url: '',
      membership_type: 'Monthly',
      start_date: new Date().toISOString().split('T')[0],
      end_date: '',
      notes: ''
    });
    setEditingMember(null);
  };

  const openAddModal = () => {
    resetForm();
    setShowModal(true);
  };

  const filteredMembers = members.filter(member =>
    member.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    member.contact.includes(searchTerm)
  );

  if (loading) return <div className="loading">Loading members...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="container">
      <div className="page-header">
        <h1>Members</h1>
        {user.role === 'Admin' && (
          <button className="btn btn-primary" onClick={openAddModal}>
            + Add Member
          </button>
        )}
      </div>

      <div className="card">
        <div className="search-box">
          <input
            type="text"
            placeholder="Search by name or contact..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <table>
          <thead>
            <tr>
              <th>Member ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Contact</th>
              <th>Age</th>
              <th>Gender</th>
              <th>Membership Type</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Status</th>
              {user.role === 'Admin' && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {filteredMembers.map((member) => (
              <tr key={member._id}>
                <td>{member.member_id || 'N/A'}</td>
                <td>{member.name}</td>
                <td>{member.email || 'N/A'}</td>
                <td>{member.contact}</td>
                <td>{member.age || 'N/A'}</td>
                <td>{member.gender || 'N/A'}</td>
                <td>{member.membership_type}</td>
                <td>{new Date(member.start_date).toLocaleDateString()}</td>
                <td>{new Date(member.end_date).toLocaleDateString()}</td>
                <td>
                  <span className={`status-badge status-${member.status.toLowerCase().replace(' ', '-')}`}>
                    {member.status}
                  </span>
                </td>
                {user.role === 'Admin' && (
                  <td>
                    <button 
                      className="btn btn-sm btn-secondary" 
                      onClick={() => handleEdit(member)}
                    >
                      Edit
                    </button>
                    <button 
                      className="btn btn-sm btn-danger" 
                      onClick={() => handleDelete(member._id)}
                      style={{ marginLeft: '5px' }}
                    >
                      Delete
                    </button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>

        {filteredMembers.length === 0 && (
          <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>
            No members found
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>{editingMember ? 'Edit Member' : 'Add New Member'}</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Contact Number *</label>
                  <input
                    type="text"
                    value={formData.contact}
                    onChange={(e) => setFormData({ ...formData, contact: e.target.value })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Age</label>
                  <input
                    type="number"
                    value={formData.age}
                    onChange={(e) => setFormData({ ...formData, age: e.target.value })}
                    min="1"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Date of Birth</label>
                  <input
                    type="date"
                    value={formData.date_of_birth}
                    onChange={(e) => setFormData({ ...formData, date_of_birth: e.target.value })}
                  />
                </div>

                <div className="form-group">
                  <label>Gender</label>
                  <select
                    value={formData.gender}
                    onChange={(e) => setFormData({ ...formData, gender: e.target.value })}
                  >
                    <option value="">Select Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>Address</label>
                <input
                  type="text"
                  value={formData.address}
                  onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                />
              </div>

              <h3 style={{ marginTop: '20px', marginBottom: '10px' }}>Membership Details</h3>
              <div className="form-row">
                <div className="form-group">
                  <label>Name</label>
                  <input
                    type="text"
                    value={formData.emergency_contact.name}
                    onChange={(e) => setFormData({ 
                      ...formData, 
                      emergency_contact: { ...formData.emergency_contact, name: e.target.value }
                    })}
                  />
                </div>

                <div className="form-group">
                  <label>Phone</label>
                  <input
                    type="text"
                    value={formData.emergency_contact.phone}
                    onChange={(e) => setFormData({ 
                      ...formData, 
                      emergency_contact: { ...formData.emergency_contact, phone: e.target.value }
                    })}
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Relationship</label>
                <input
                  type="text"
                  value={formData.emergency_contact.relationship}
                  onChange={(e) => setFormData({ 
                    ...formData, 
                    emergency_contact: { ...formData.emergency_contact, relationship: e.target.value }
                  })}
                  placeholder="e.g., Spouse, Parent, Sibling"
                />
              </div>

              <h3 style={{ marginTop: '20px', marginBottom: '10px' }}>Membership Details</h3>
              <div className="form-group">
                <label>Membership Type *</label>
                <select
                  value={formData.membership_type}
                  onChange={(e) => setFormData({ ...formData, membership_type: e.target.value })}
                  required
                >
                  <option value="Monthly">Monthly</option>
                  <option value="Quarterly">Quarterly</option>
                  <option value="Annual">Annual</option>
                </select>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Start Date *</label>
                  <input
                    type="date"
                    value={formData.start_date}
                    onChange={(e) => setFormData({ ...formData, start_date: e.target.value })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>End Date *</label>
                  <input
                    type="date"
                    value={formData.end_date}
                    onChange={(e) => setFormData({ ...formData, end_date: e.target.value })}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Notes / Remarks</label>
                <textarea
                  value={formData.notes}
                  onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
                  rows="3"
                  placeholder="Any additional information..."
                  style={{ width: '100%', padding: '10px', borderRadius: '4px', border: '1px solid #ddd' }}
                />
              </div>

              <div className="modal-actions">
                <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  {editingMember ? 'Update' : 'Add'} Member
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Members;
