import React, { useState, useEffect } from 'react';
import { paymentsAPI, membersAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import './Payments.css';

function Payments() {
  const [payments, setPayments] = useState([]);
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    member_id: '',
    amount: '',
    membership_plan: 'Monthly'
  });

  const membershipPrices = {
    Monthly: 500,
    Quarterly: 1350,
    Annual: 5000
  };

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    // Auto-fill amount based on membership plan
    if (formData.membership_plan) {
      setFormData(prev => ({
        ...prev,
        amount: membershipPrices[formData.membership_plan]
      }));
    }
  }, [formData.membership_plan]);

  const loadData = async () => {
    try {
      const [paymentsRes, membersRes] = await Promise.all([
        paymentsAPI.getAll(),
        membersAPI.getAll()
      ]);
      setPayments(paymentsRes.data);
      setMembers(membersRes.data);
      setLoading(false);
    } catch (err) {
      setMessage({ type: 'error', text: 'Failed to load data' });
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await paymentsAPI.create(formData);
      setMessage({ type: 'success', text: 'Payment recorded successfully!' });
      setShowModal(false);
      resetForm();
      loadData();
      setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    } catch (err) {
      setMessage({ 
        type: 'error', 
        text: err.response?.data?.message || 'Failed to record payment' 
      });
    }
  };

  const resetForm = () => {
    setFormData({
      member_id: '',
      amount: '',
      membership_plan: 'Monthly'
    });
  };

  const openModal = () => {
    resetForm();
    setShowModal(true);
  };

  if (loading) return <div className="loading">Loading payments...</div>;

  return (
    <div className="container">
      <div className="page-header">
        <h1>Payments</h1>
        <button className="btn btn-primary" onClick={openModal}>
          + Record Payment
        </button>
      </div>

      {message.text && (
        <div className={`alert alert-${message.type}`}>
          {message.text}
        </div>
      )}

      <div className="card">
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Member</th>
              <th>Plan</th>
              <th>Amount</th>
              <th>Staff</th>
            </tr>
          </thead>
          <tbody>
            {payments.map((payment) => (
              <tr key={payment._id}>
                <td>{new Date(payment.payment_date).toLocaleString()}</td>
                <td>{payment.member_name}</td>
                <td>{payment.membership_plan}</td>
                <td>₱{payment.amount.toLocaleString()}</td>
                <td>{payment.staff_name}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {payments.length === 0 && (
          <div style={{ padding: '20px', textAlign: 'center', color: '#666' }}>
            No payment records found
          </div>
        )}
      </div>

      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>Record Payment</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Select Member *</label>
                <select
                  value={formData.member_id}
                  onChange={(e) => setFormData({ ...formData, member_id: e.target.value })}
                  required
                >
                  <option value="">-- Select Member --</option>
                  {members.map((member) => (
                    <option key={member._id} value={member._id}>
                      {member.name} - {member.contact} ({member.status})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Membership Plan *</label>
                <select
                  value={formData.membership_plan}
                  onChange={(e) => setFormData({ ...formData, membership_plan: e.target.value })}
                  required
                >
                  <option value="Monthly">Monthly - ₱500</option>
                  <option value="Quarterly">Quarterly - ₱1,350</option>
                  <option value="Annual">Annual - ₱5,000</option>
                </select>
              </div>

              <div className="form-group">
                <label>Amount (₱) *</label>
                <input
                  type="number"
                  value={formData.amount}
                  onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
                  required
                  min="0"
                  step="0.01"
                />
              </div>

              <div className="payment-info">
                <p><strong>Note:</strong> Payment will automatically activate/extend the member's subscription based on the selected plan.</p>
              </div>

              <div className="modal-actions">
                <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-success">
                  Record Payment
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Payments;
